plain.example:
const { Clone } = require('lopo-lib')

const oriObj = {
    type_number: 12345,
    type_string: 'i am string',
    type_boolean: false,
    type_undefined: undefined,
    type_null: null,
    type_array: [1, 2, 3, 4, 5],
    type_object: {
        attr1: 1,
        attr2: '2'
    },
    type_function: () => {
        console.log('i am function')
    }
}

const plainRes = Clone.plain(oriObj)
console.log(plainRes)

// the result in console is:
// {
//     type_number: 12345,
//     type_string: 'i am string',
//     type_boolean: false,
//     type_null: null,
//     type_array: [ 1, 2, 3, 4, 5 ],
//     type_object: { attr1: 1, attr2: '2' }
// }

