in.example:
const { Queue } = require('lopo-lib')

const queue = new Queue(5)

// 1. push some items into the queue
let count = queue.in('item0', 'item1', 'item2')
console.log(queue.getQueue(), count)
// ['item0', 'item1', 'item2'] 3

// 2. push more items
count = queue.in('item3', 'item4', 'item5')
console.log(queue.getQueue(), count)
// ['item1', 'item2', 'item3', 'item4', 'item5'] 5
// and the the first item 'item0' was pushed out of the queue

// 3. push more items (more then max-length of the queue)
count = queue.in('item6', 'item7', 'item8', 'item9', 'item10', 'item11')
console.log(queue.getQueue(), count)
// ['item7', 'item8', 'item9', 'item10', 'item11'] 5
// only the last 5 items in the queue will be kept

out.example:
const { Queue } = require('lopo-lib')

const queue = new Queue(5)
queue.in('item0', 'item1', 'item2', 'item3', 'item4')

// 1. take some items from the queue
let take = queue.out(3)
console.log(take)
// ['item0', 'item1', 'item2'] -- that is what we need

// 2. take more items from the queue (more than what's left in the queue)
take = queue.out(3)
console.log(take)
// ['item3', 'item4'] -- it just takes out the rest

// 3. take some items from the empty queue
take = queue.out(1)
console.log(take)
// [] -- returns an empty array

circle.example:
const { Queue } = require('lopo-lib')

const queue = new Queue(5)
queue.in('item0', 'item1', 'item2')

// 1. rotate forward 1 position
queue.circle(1)
console.log(queue.getQueue())
// ['item1', 'item2', 'item0']

// 2. reverse rotation 2 position
queue.circle(2, true)
console.log(queue.getQueue())
// ['item2', 'item0', 'item1']

clear.example:
const { Queue } = require('lopo-lib')

const queue = new Queue(5)
queue.in('item0', 'item1', 'item2', 'item3', 'item4', 'item5')
console.log(queue.getQueue(), queue.length)
// ['item1', 'item2', 'item3', 'item4', 'item5'] 5

queue.clear(3)
queue.in('item0', 'item1', 'item2', 'item3', 'item4', 'item5')
console.log(queue.getQueue(), queue.length)
// ['item3', 'item4', 'item5'] 3 -- now the max-length of the queue has been set to 3

